import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import Header from "../Header";
import Footer from "../Footer";
import ImageSlider from "../../ImageSlider";
import "./DogRunDetail.css";

interface Store {
  store_id: number;
  store_name: string;
  store_description: string;
  store_address: string;
  store_opening_hours: string;
  store_phone_number: string;
  store_url: string;
  store_img: string[];
  tags: string[];
  reviews?: Review[];
}

interface Review {
  id: number;
  store_id: number;
  rating: number;
  comment: string;
}

const getUserIdFromCookie = (): number | null => {
  const cookies = document.cookie.split("; "); // クッキーを分割
  for (let cookie of cookies) {
    const [name, value] = cookie.split("="); // クッキー名と値を分割
    if (name === "user_id") {
      const parsedValue = parseInt(decodeURIComponent(value), 10); // URIデコードしてから数値に変換
      return isNaN(parsedValue) ? null : parsedValue; // NaNの場合はnullを返す
    }
  }
  return null; // 該当するクッキーが存在しない場合
};


const DogRunDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [store, setStore] = useState<Store | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [isFavorite, setIsFavorite] = useState<boolean>(false);
  const [userId, setUserId] = useState<number | null>(0);
  const MAP_API_KEY = process.env.REACT_APP_MAP_API_KEY;

  useEffect(() => {
    const userIdFromCookie = getUserIdFromCookie();
    setUserId(userIdFromCookie); // `number | null` の型で渡す
  }, []);

useEffect(() => {
  const fetchStoreAndFavorite = async () => {
    try {
      const storeResponse = await fetch(`http://localhost:5003/stores/detail/${id}`);
      if(!storeResponse.ok) throw new Error("店舗情報の取得失敗しました");
      const storeData: Store = await storeResponse.json()

      setStore(storeData);

    //お気に入り情報の取得
      if(userId) {
        const favoriteResponse = await fetch(`http://local:5003/favorite/${userId}`);
        if(favoriteResponse.ok) {
          const favoriteData: {store_id: number} [] = await favoriteResponse.json();
          setIsFavorite(
            favoriteData.some((fav) => fav.store_id === Number (id))
          );
        }
      }
    } catch (err: any) {
      console.error("データ取得中にエラー:", err);
      setError("情報の取得に失敗しました");
    }
  };

  if (id) fetchStoreAndFavorite();
},[id, userId]);



//----------------------
const handleFavoriteClick = async () => {
  if (!store || userId === null) return;

  const url = "http://localhost:5003/favorites";
  const method = isFavorite ? "DELETE" : "POST";
  const body = JSON.stringify({
      user_id: userId,
      store_id: store.store_id,
  });

  try {
    const response = await fetch(url, {
      method,
      headers: { "Content-Type": "application/json"},
      body,
    });
    if (!response.ok) throw new Error("お気に入りの更新に失敗しました");
  setIsFavorite(!isFavorite);
  } catch (err) {
    setError("お気に入りの更新に失敗しました");
  }
};

  if (error) return <div className="container">{error}</div>;
  if (!store) return <div className="container">データを読み込んでいます..</div>;

  // 平均評価の計算
  const averageRating =
    store.reviews && store.reviews.length > 0
      ? store.reviews.reduce((sum, rev) => sum + rev.rating, 0) /
        store.reviews.length
      : 0;

  return (
    <>
      <Header />
      <div className="detail-container">
        <h1 className="detail-title">{store.store_name}</h1>
        {store.store_img.length > 0 ? (
          <ImageSlider images={store.store_img} />
        ) : (
          <p>画像がありません</p>
        )}
        {store.reviews && store.reviews.length > 0 && (
          <Link
            to={`/dogrun/reviews/${store.store_id}`}
            className="review-button"
          >
            口コミを見る
          </Link>
        )}

        {/* 平均評価を星で表示 */}
        <div style={{ margin: "20px 0" }}>
          {store.reviews && store.reviews.length > 0 ? (
            <>
              <div style={{ fontSize: "24px", color: "gray" }}>
                {[1, 2, 3, 4, 5].map((value) => (
                  <span
                    key={value}
                    className={`star ${
                      value <= Math.round(averageRating) ? "selected" : ""
                    }`}
                  >
                    ★
                  </span>
                ))}
              </div>
              <p style={{ fontSize: "14px", fontWeight: "bold" }}>
                {averageRating.toFixed(1)}
              </p>
            </>
          ) : (
            <p>まだ口コミはありません</p>
          )}
        </div>
        {/* 店舗情報 */}
        <p>
          <strong>住所: </strong>
          {store.store_address}
        </p>
        {/* Google Map 埋め込み */}
        {MAP_API_KEY && (
          <div style={{ margin: "20px 0" }}>
            <iframe
              title="Google Map"
              width="100%"
              height="300"
              style={{ border: "0", borderRadius: "8px" }}
              src={`https://www.google.com/maps/embed/v1/place?key=${MAP_API_KEY}&q=${encodeURIComponent(
                store.store_address
              )}`}
              allowFullScreen
            ></iframe>
          </div>
        )}
        <p>電話番号: {store.store_phone_number}</p>
        <p>営業時間: {store.store_opening_hours}</p>

        <br />
        {/* お気に入りボタン */}
          <button
          onClick={handleFavoriteClick}
          className={`favorite-button ${isFavorite ? "active" : ""}`}
        >
          {isFavorite ? "お気に入り解除" : "お気に入り登録"}
        </button>
        <br />
        <a
          href={store.store_url}
          target="_blank"
          rel="noopener noreferrer"
          className="official-site"
        >
          店舗の公式サイト
        </a>
      </div>
      <Footer />
    </>
  );
};

export default DogRunDetail;
