    <div className="petshop-page">
     <h2 className="petshop-title">全国のペットショップを探す</h2>
     <div className="dogcafe-img">
      <img src={petshop} 
      alt="ペットショップのイラスト"
      className="petshop-illust" />
     </div>
    </div>
  );
};
export default PetshopPage;
