import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

interface Store {
  store_id: number;
  store_name: string;
  store_description: string;
  store_address: string;
  store_opening_hours: number;
  store_phone_number: string;
  store_dogrun_detail: string;
  store_url: string;
  store_img: string;
  tags: string[];
  }

    const DogRunDetail: React.FC = () => {
      const { storeId } = useParams<{ storeId: string }>();
      const [store, setStore] = useState<Store | null >(null);


    //店舗データの取得
      useEffect(() => {
        const fetchStores= async () => {
          try {
            const response = await fetch(`http:/localhost:5003/store/detail/${storeId}`);
            const data : Store[] = await response.json();
            setStore(data[0]);
          } catch (error) {
            console.error("店舗情報の取得に失敗しました:", error); 
        }
      };
      fetchStores();
    },[storeId]);

    if (!store) {
      return <div>店舗データがありません</div>;
    }


      return (
        <div>
          <h1>{store.store_name}</h1>
          <img src= {store.store_img} alt= {store.store_name} />
          <p>{store.store_description}</p>
          <p>住所: {store.store_address}</p>
          <p>電話番号: {store.store_phone_number}</p>
          <a href={store.store_url} target="_blank" rel="noopener noreferrer">
            店舗の公式サイト
          </a>
        <ul>
          {store.tags.map((tag, index) => (
          <li key={index}>{tag}</li>
            ))}
         </ul>
        </div>
      );
    };


  export default DogRunDetail;
