import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./TopPage.css";
import HamburgerMenu from "../HamburgerMenu";
import DogrunImage from "../pages/assets/images/Dogrun/dogrun.png";
import dogcafeImage from "../pages/assets/images/Dogcafe/dogcafe.png";
import petshopImage from "../pages/assets/images/Petshop/petshop.png";
import hospitalImage from "../pages/assets/images/Hospital/hospital.png";

const images = [
  { name: "dogrun", src: DogrunImage },
  { name: "dogcafe", src: dogcafeImage },
  { name: "petshop", src: petshopImage },
  { name: "hospital", src: hospitalImage },
];

const TopPage: React.FC = () => {
  const [showText, setShowText] = useState(false);

  useEffect(() => {
    setTimeout(() => {
      setShowText(true);
    }, 800);
  }, []);

  return (
    <div className="TopPage-container">
      <HamburgerMenu />
      <header className="App-header">Header</header>

      <p className={`main-title scale-up ${showText ? "show" : ""}`}>
        ドッグラン、ドッグカフェ、ペットショップや動物病院など
        <br />
        様々なわんこの情報をご紹介しております!
      </p>

      {/* 無限スクロールスライダー */}
      <div className="infinite-slider">
        <div className="slider-track">
          {images.map((image, index) => (
            <div className="slide" key={index}>
              <img src={image.src} alt={image.name} />
            </div>
          ))}
          {images.map((image, index) => (
            <div className="slide" key={`duplicate-${index}`}>
              <img src={image.src} alt={image.name} />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TopPage;
