import React, { useState } from "react";
import "./Modal.css";

interface ModalProps {
  show: boolean;
  onClose: () => void;
  onSubmit: (rating: number, comment: string) => void;
  storeId: number;
}

const Modal: React.FC<ModalProps> = ({ show, onClose, onSubmit }) => {
  const [rating, setRating] = useState<number>(5);
  const [comment, setComment] = useState<string>("");

  if (!show) {
    return null;
  }

  const handleReviewSubmit = async (rating: number, comment: string) => {
    const newReviewData = {
      store_id: Number(storeId),
      rating,
      comment: comment.trim(),
    };

    console.log("送信データ:", newReviewData); // 送信前にデータ確認

    try {
      const response = await fetch("http://localhost:5003/reviews", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newReviewData),
      });

      if (!response.ok) {
        const errorResponse = await response.json();
        console.error("バックエンドエラー:", errorResponse); // バックエンドのエラー詳細
        throw new Error("コメント投稿に失敗しました");
      }

      const createdReview = await response.json();
      console.log("投稿成功:", createdReview);
      setReviews((prev) => [...prev, createdReview]);
      setShowModal(false);
    } catch (error) {
      console.error("フロントエンドエラー:", error);
      setError("コメント投稿に失敗しました");
    }
  };


  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>&times;</span>
        <h2>コメントを投稿</h2>
        <form onSubmit={handleReviewSubmit} style={{ marginTop: '20px' }}>
          <div style={{ marginBottom: '10px' }}>
            <label>
              評価:
              <input
                type="number"
                value={rating}
                onChange={(e) => setRating(Number(e.target.value))}
                min={1}
                max={5}
                style={{ marginLeft: '10px', width: '60px' }}
              />
            </label>
          </div>
          <div style={{ marginBottom: '10px' }}>
            <label>
              コメント:
              <textarea
                value={comment}
                onChange={(e) => setComment(e.target.value)}
                style={{ width: '100%', height: '80px', marginLeft: '10px' }}
              />
            </label>
          </div>
          <button type="submit" style={{ padding: '8px 16px', borderRadius: '4px' }}>投稿</button>
        </form>
      </div>
    </div>
  );
};

export default Modal;
