import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

interface Store {
  id: number;
  name: string;
  description: string;
  address: string;
  phone_number: string;
  store_url: string;
  store_img: string;
  tags: Tag[];
}

interface Tag {
  id: number;
  name: string;
}

const DogRunStoreList: React.FC = () => {
  const { prefectureId } = useParams<{ prefectureId: string }>();
  const navigate = useNavigate();
  const [stores, setStores] = useState<Store[]>([]);
  const [tags, setTags] = useState<Tag[]>([]);
  const [selectedTagIds, setSelectedTagIds] = useState<number[]>([]);
  const [selectedPrefecture, setSelectedPrefecture] = useState<string>("");
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);

  const prefectureNames: { [key: string]: string } = {
    "1": "北海道",
    "60": "東京",
    "61": "神奈川",
    "70": "愛知",
    "73": "京都",
    "74": "大阪",
    "75": "兵庫",
  };

  useEffect(() => {
    // 選択された都道府県名をセット
    setSelectedPrefecture(
      prefectureNames[prefectureId ?? ""] || "ドッグラン情報がありません"
    );

    // タグデータの取得
    const fetchTags = async () => {
      setLoading(true);
      try {
        const response = await fetch("http://localhost:5003/tags");
        const data = await response.json();
        setTags(data);
      } catch (error) {
        setError("タグ情報の取得に失敗しました");
      } finally {
        setLoading(false);
      }
    };

    fetchTags();
  }, [prefectureId]);

  // タグがクリックされたときのハンドラー
  const handleTagClick = async (tagId: number) => {
    setSelectedTagIds([tagId]);
    setLoading(true);
    try {
      // 選択したタグに基づいて該当する店舗をバックエンドから取得
      const response = await fetch(
        `http://localhost:5003/stores/by-tag/${tagId}`
      );
      const data = await response.json();

      if (data.length > 0) {
        // 該当する店舗がある場合、最初の店舗の詳細ページに遷移
        navigate(`/store/${data[0].store_id}`);
      } else {
        setError("該当するドッグランが見つかりませんでした。");
      }
    } catch (error) {
      setError("店舗情報の取得に失敗しました。");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      style={{
        textAlign: "center",
        padding: "20px",
        backgroundColor: "#FAF3E0",
      }}
    >
      {error && (
        <div style={{ color: "red", marginBottom: "20px" }}>{error}</div>
      )}
      {loading && <p>読み込み中...</p>}

      <h2>{selectedPrefecture}のドッグラン</h2>
      <p style={{ fontSize: "14px", marginBottom: "20px" }}>
        行きたいドッグランの条件を探す
      </p>

      {/* タグボタン */}
      <div
        style={{
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "center",
          gap: "10px",
        }}
      >
        {tags.map((tag) => (
          <button
            key={tag.id}
            onClick={() => handleTagClick(tag.id)}
            style={{
              backgroundColor: selectedTagIds.includes(tag.id)
                ? "#D1E8E2"
                : "#FFF",
              color: "#333",
              padding: "10px 15px",
              border: "1px solid #333",
              borderRadius: "20px",
              cursor: "pointer",
              fontSize: "15px",
              fontFamily: "Noto sans jp",
            }}
          >
            {tag.name}
          </button>
        ))}
      </div>
      {filteredStores.length > 0 ? (
        <>
          <div style={{ marginTop: "30px" }}>
            {filteredStores.map((store) => (
              <div key={store.id} style={{ marginBottom: "20px" }}>
                <h3>{store.name}</h3>
                <p>{store.description}</p>
              </div>
            ))}
          </div>
        </>
      ) : (
        <>
          <h2>ドッグラン情報がありません</h2>
          <p>該当するドッグランが見つかりません。</p>
        </>
      )}
    </div>
  );
};

export default DogRunStoreList;
