import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useModal } from "../../context/ModalContext"; 
import "./DogRunReviewList.css";
import "../../context/ModalContext";
import HamburgerMenu from "../../HamburgerMenu";
import Header from "../Header";
import Modal  from "../../components/Modal";



type Review = {
  id: number;
  name: string;
  store_id: number;
  rating: number;
  comment: string;
  date: string;
  created_at: string;
  updated_at: string;
};

const DogrunReview: React.FC = () => {
  const { storeId } = useParams<{ storeId: string }>();
  const { openModal } = useModal();
  const [reviews, setReviews] = useState<Review[]>([]);
  const [error, setError] = useState<string | null>(null);
  const [averageRating, setAverageRating] = useState<number>(0);
  const [storeName, setStoreName] = useState<string>("");

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const response = await fetch("http://localhost:5003/reviews");
        const data = await response.json();
        const storedData = data.sort(
          (a: Review, b: Review) =>
            new Date(b.created_at).getTime() - new Date(a.created_at).getTime()
        );

        const avgRating = storedData.length
          ? storedData.reduce(
              (sum: number, review: Review) => sum + review.rating,
              0
            ) / storedData.length
          : 0;

        setReviews(storedData);
        setAverageRating(Number(avgRating.toFixed(1)));
      } catch (error) {
        console.error(error);
        setError("口コミの取得に失敗しました");
      }
    };

    const fetchStoreName = async () => {
      try {
        const response = await fetch(
          `http://localhost:5003/stores/store-name/${storeId}`
        );
        const data = await response.json();
        setStoreName(data.store_name);
      } catch (error) {
        setError("店舗情報の取得に失敗しました");
      }
    };

    fetchReviews();
    fetchStoreName();
  }, [storeId]);

  return (
    <>
      <Header />
      <div className="review-container">
        <h1 className="store-name">{storeName || "店舗名を取得中"}</h1>
        <div className="star-container">
          <div className="stars-background">★★★★★</div>
          <div
            className="stars-filled"
            style={{
              width: `${(averageRating / 5) * 100}%`,
            }}
          >
            ★★★★★
          </div>
        </div>
        <span className="average-rating-value">{averageRating.toFixed(1)}</span>

        <h2 className="review-title">口コミ一覧</h2>
        <button onClick={() => openModal(storeName)} style={{ marginTop: "20px" }}>
          投稿
        </button>
      </div>
    </>
  );
};

export default DogrunReview;
