import React, { useEffect, useState } from "react";
import "../pages/FavoritePage.css";
import { Link } from "react-router-dom";
import Header from "./Header";
import Footer from "./Footer";

interface Favorite {
  id: number;
  user_id: number;
  store_id: number;
  store_name: string;
  store_address: string;
  store_URL: string;
  store_img: string;
  store_type: number;
}

const FavoritePage: React.FC = () => {
  const [favorites, setFavorites] = useState<Favorite[]>([]);

  useEffect(() => {
    const fetchFavorites = async () => {
      try {
        const response = await fetch("http://localhost:5003/favorites/1", {
          method: "GET",
          credentials: "include",
          headers: { "Content-Type": "application/json" },
        });

        if (!response.ok) {
          throw new Error("エラーが返されました");
        }

        const data: Favorite[] = await response.json();
        console.log("📌 取得したお気に入りリスト:", data);

        setFavorites(data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchFavorites();
  }, []);

  useEffect(() => {
    console.log("📌 State更新後の favorites:", favorites);
  }, [favorites]);

  const getCategoryTitle = (storeType: number) => {
    switch (storeType) {
      case 1:
        return "🐶 ドッグラン";
      case 2:
        return "☕ ドッグカフェ";
      case 3:
        return "🛍️ ペットショップ";
      case 4:
        return "🏥 動物病院";
      default:
        return "その他";
    }
  };

  const getCategoryPath = (storeType: number, storeId: number) => {
    switch (storeType) {
      case 1:
        return `/dogrun/detail/${storeId}`;
      case 2:
        return `/dogcafe/detail/${storeId}`;
      case 3:
        return `/petshop/detail/${storeId}`;
      case 4:
        return `/hospital/detail/${storeId}`;
      default:
        return `/detail/${storeId}`;
    }
  };

  const renderCategory = (storeType: number) => {
    const items = favorites?.filter((fav) => fav.store_type === storeType) || [];

    return (
      <section key={storeType}>
        <h2 className="category-title">{getCategoryTitle(storeType)}</h2>
        {items.length > 0 ? (
          <ul className="favorite-list">
            {items.map((favorite) => (
              <Link
                to={getCategoryPath(favorite.store_type, favorite.store_id)}
                className="favorite-link"
                key={favorite.store_id}
              >
                <li className="favorite-item">
                  <img
                    src={
                      favorite.store_img
                        ? favorite.store_img
                        : "https://via.placeholder.com/150"
                    }
                    alt={favorite.store_name}
                    className="favorite-image"
                  />
                  <h2 className="favorite-title">{favorite.store_name}</h2>
                </li>
              </Link>
            ))}
          </ul>
        ) : (
          <p className="no-favorites">このカテゴリーにお気に入りはまだ登録されていません。</p>
        )}
      </section>
    );
  };

  return (
    <>
      <Header />
      <div className="favorite-container">
        <header className="app-header">
          <h1 className="title">お気に入りリスト</h1>
        </header>

        {renderCategory(1)}
        {renderCategory(2)}
        {renderCategory(3)}
        {renderCategory(4)}
      </div>
      <Footer />
    </>
  );
};

export default FavoritePage;
