import React, { useState } from "react";
import { Link, useParams, useNavigate, useLocation } from "react-router-dom";
import "./PetshopPage.css";
import petshop from "../assets/images/petshop.png";


const prefectureNames : { [key: string]: string } = {
  hokkaido: "北海道",
  tohoku: "東北地方",
  kanto: "関東地方",
  chubu: "中部地方",
  kansai: "関西地方",
  chugoku: "中国地方",
  shikoku: "四国地方",
  kyushu: "九州地方",
  okinawa: "沖縄",

 };

// PetshopPage コンポーネント（都道府県別のページ）
const PetshopPage: React.FC = () => {
  const { prefecture } = useParams<{ prefecture: string } >();
  const location = useLocation();
  const navigate = useNavigate();
  const [selectedOptions,setSelectedOptions] = useState<string[]>([]);
  const [isDropdownVisible, setIsDropdownVisible] = useState(false)
    // ドロップダウンの表示/非表示を切り替える
  const toggleDropdown = () => {
    setIsDropdownVisible(!isDropdownVisible);
  };

const handleOptionClick = (option: string) => {
  setSelectedOptions((prevSelected) =>
    prevSelected.includes(option) ? prevSelected.filter((item) => item ! == option) : [...prevSelected,option] 
);
};


      return (
      <div className="petshop-detail-page">
        <header className="header">
          <Link to="/" style={{textDecoration: "none",color:"black"}}>
          wan paradise
          </Link>
          </header>
        <div className="tab-container" onClick={toggleDropdown}>
          <span>全国のペットショップを探す</span>
          <div className="dogcafe-img">
            <img src={petshop}
              alt="ペットショップのイラスト"
              className="petshop-illust" />
          </div>
        </div>
        {/* ドロップダウンメニューの表示 */}
        {isDropdownVisible && (
          <div className="dropdown-menu">
            <ul>
              <li>
                <Link to="/dogrun/hokkaido">北海道</Link>
              </li>
              <li>
                <Link to="/dogrun/tohoku">東北</Link>
              </li>
              <li>
                <Link to="/dogrun/kanto">関東</Link>
              </li>
              <li>
                <Link to="/dogrun/chubu">中部</Link>
              </li>
              <li>
                <Link to="/dogrun/kansai">関西</Link>
              </li>
              <li>
                <Link to="/dogrun/chugoku">中国</Link>
              </li>
              <li>
                <Link to="/dogrun/shikoku">四国</Link>
              </li>
              <li>
                <Link to="/dogrun/kyushu">九州</Link>
              </li>
              <li>
                <Link to="/dogrun/okinawa">沖縄</Link>
              </li>
            </ul>
          </div>
        )}

      </div>
      );
};
      export default  PetshopPage;
