import React, { useState } from "react";
import "./HamburgerMenu.css";

const HamburgerMenu: React.FC = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);


  const toggleMenu = () => {
    setIsMenuOpen((prevState) => !prevState);
  };

  // メニューを閉じる関数
  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <div className="hamburger-menu">
      {/* ハンバーガーアイコン */}
      <button
        className={`menu-button ${isMenuOpen ? "open" : ""}`}
        aria-label="メニューを開く"
        onClick={toggleMenu}
      >
        <span className="menu-icon"></span>
      </button>

      {/* モーダルの背景 */}
      {isMenuOpen && <div className="menu-backdrop" onClick={closeMenu}></div>}

      {/* メニュー本体 */}
      <nav className={`menu ${isMenuOpen ? "open" : ""}`}>
        <button className="close-button" onClick={closeMenu} aria-label="閉じる">
          ✕
        </button>
        <ul>
          <li><a href="/favorites">お気に入り</a></li>
          <li><a href="/dogrun">ドッグラン</a></li>
          <li><a href="/dogcafe">ドッグカフェ</a></li>
          <li><a href="/petshop">ペットショップ</a></li>
          <li><a href="/hospital">動物病院</a></li>
        </ul>
      </nav>
    </div>
  );
};

export default HamburgerMenu;
