import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import "./DogRunDetail.css";

interface Store {
  store_id: number;
  store_name: string;
  store_description: string;
  store_address: string;
  store_opening_hours: string;
  store_phone_number: string;
  store_url: string;
  store_img: string;
  tags: string[]; // タグの配列として定義
  reviews?: Review[];
}

interface Review {
  id: number;
  store_id: number;
  rating: number;
  comment: string;
}

const DogRunDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [store, setStore] = useState<Store | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [selectedRating, setSelectedRating] = useState<number>(0);
  const [averageRating, setAverageRating] = useState<number>(0);
  //お気に入り状態を管理
  const [isFavorite, setIsFavorite] = useState<boolean>(false);

  const userId = 1; //仮のユーザーID

// 初期表示: お気に入り状態を取得
  useEffect(() => {
    const fetchFavoriteStatus = async () => {
      try {
        const response = await fetch(`http://localhost:5000/favorites/${userId}`);
        if (!response.ok) throw new Error("お気に入りデータの取得に失敗しました");

        const favorites = await response.json();
        const isAlreadyFavorite = favorites.some(
          (fav: { store_id: number }) => fav.store_id === Number(id)
        );
        setIsFavorite(isAlreadyFavorite);
      } catch (error) {
        console.error("お気に入り取得エラー:", error);
      }
    };

    fetchFavoriteStatus();
  }, [id]);

// 店舗情報の取得
useEffect(() => {
  const fetchStore = async () => {
    try {
      const response = await fetch(`http://localhost:5003/stores/detail/${id}`);
      if (!response.ok) throw new Error("店舗情報の取得に失敗しました");
      const data = await response.json();
      setStore(data);
    } catch (error) {
      console.error("店舗情報取得エラー:", error);
    }
  };

  fetchStore();
}, [id]);




  //MAP
  const MAP_API_KEY = "AIzaSyBamO2vaf6PMNHy3bhMJd_2FO2I6IUgEpE";


  const handleStarClick = (value: number) => {
    if (selectedRating === value) {
      setSelectedRating(0);
    } else {
      setSelectedRating(value);
    }
  };


  useEffect(() => {
    const fetchStores = async () => {
      try {
        const response = await fetch(
          `http://localhost:5003/stores/detail/${id}`
        );
        if (!response.ok) {
          throw new Error(`サーバーエラー: ${response.status}`);
        }
        const data = await response.json();
        console.log(data);

        setStore(data);
      } catch (error) {
        console.error("店舗情報の取得に失敗しました:", error);
      }
    };
    fetchStores();
  }, [id]);

  // 店舗データとレビューを取得して設定する関数

  useEffect(() => {
    const fetchStoreWithReviews = async () => {
      try {
        const storeResponse = await fetch(`http://localhost:5003/stores/detail/${id}`);
        const reviewResponse = await fetch(`http://localhost:5003/reviews`);

        if (!storeResponse.ok || !reviewResponse.ok) {
          throw new Error("データ取得に失敗しました");
        }

        const storeData: Store = await storeResponse.json();
        const reviewData: Review[] = await reviewResponse.json();

        // 店舗に関連付けられた口コミを結び付ける
        const reviews = reviewData.filter((review) => review.store_id === storeData.store_id);
        setStore({ ...storeData, reviews });
      } catch (err: any) {
        console.error("データ取得中にエラーが発生しました:", err);
        setError("店舗情報の取得に失敗しました");
      }
    };

    fetchStoreWithReviews();
  }, [id]);




  if (error) {
    return (
      <div style={{ padding: "20px", backgroundColor: "#FAF3E0", textAlign: "center" }}>
        <p>{error}</p>
      </div>
    );
  }

  // storeがnullの場合の処理
  if (!store) {
    return (
      <div>
        <p>データを読み込んでいます..🐕</p>
      </div>
    );
  }
  return (
    <div className="container">
      <header className="App-header">
        <h1>Wan Paradise</h1>
      </header>
      <h1>{store.store_name}</h1>
      {/*お気に入りボタン*/}
      <button className={`favorite-button ${isFavorite ? "active" : ""}`} onClick={handleFavoriteClick}>
        {isFavorite ? "お気に入り解除" : "お気に入り追加"}
      </button>

      <div className="average-rating">
        {[1, 2, 3, 4, 5].map((value) => (
          <span
            key={value}
            className={`star ${value <= Math.round(averageRating) ? "selected" : ""}`}
          >
            ★
          </span>
        ))}
      </div>
      <span className="average-rating-value">
        {averageRating.toFixed(1)} 
      </span>
      <img src={store.store_img} alt={store.store_name} />
      <p className="store-title">自然に囲まれた2000坪のドッグランで、大型犬でも楽しめます</p>
      <p>
        <strong>住所: </strong>
        {store.store_address}
      </p>

      <p>電話番号: {store.store_phone_number}</p>
      <p>営業時間: {store.store_opening_hours}</p>
      {/*/Google map 埋め込み*/}
      <iframe
        title="Google Map"
        width="100%"
        height="300"
        style={{ border: "0", borderRadius: "8px" }}
        src={`https://www.google.com/maps/embed/v1/place?key=${MAP_API_KEY}&q=${encodeURIComponent(
          store.store_address
        )}`}
        allowFullScreen
      ></iframe>
      {store.reviews && store.reviews.length > 0 ? (
        store.reviews.map((review) => (
          <div key={review.id}>

          </div>
        ))
      ) : (
        <p>まだ口コミはありません</p>
      )}
      {/*口コミ一覧ページへのリンクを追加*/}
      {store.reviews && store.reviews.length > 0 && (
        <Link
          to={`/dogrun/reviews/${store.store_id}`} className="link" >
          口コミを見る
        </Link>
      )}
      <br /><br />
      <a
        href={store.store_url}
        target="_blank"
        rel="noopener noreferrer">
        店舗の公式サイト
      </a>
    </div>
  );
};

export default DogRunDetail;
