import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import Header from "../Header";
import "../Header.css";
import ImageSlider from "ImageSlider";


interface Store {
  store_id: number;
  store_name: string;
  store_description: string;
  store_address: string;
  store_opening_hours: string;
  store_phone_number: string;
  store_url: string;
  store_img: string[];
  tags: string[]; // タグの配列として定義
  reviews?: Review[];
}

interface Review {
  id: number;
  store_id: number;
  rating: number;
  comment: string;
}

const PetShopDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [store, setStore] = useState<Store | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [isFavorite, setIsFavorite] = useState<boolean>(false);
  const [userId, setUserId] = useState<number | null>(null);

//MAP
  const MAP_API_KEY = process.env.REACT_APP_MAP_API_KEY;



  //店舗情報とお気に入り情報の取得
  useEffect(() => {
    if (!userId) return;

    const fetchStoreAndFavorite = async () => {
      try {
        //店舗データ取得
        const storeResponse = await fetch(`http://localhost:5003/stores/detail/${id}`);
        if (!storeResponse.ok) throw new Error("店舗情報の取得に失敗しました");

        const storeData: Store = await storeResponse.json();
        setStore(storeData);

        //お気に入り状態を取得
        const favoriteResponse = await fetch(`http://localhost:5003/favorites/${userId}`);

        const favoriteData: { store_id: number }[] = await favoriteResponse.json();
        setIsFavorite(favoriteData.some((fav) => fav.store_id === storeData.store_id));
      } catch (err: any) {

        console.error(err.message);
        setError("データの取得に失敗しました");
      }
    };
    fetchStoreAndFavorite();
  }, [id, userId]);


  // お気に入りの追加・解除
  const handleFavoriteClick = async () => {
    console.log("現在のユーザーID:", userId); // userId の確認
    console.log("現在の店舗ID:", store?.store_id); // store_id の確認
    if (!userId || !store?.store_id) {
      console.error("ユーザーID または 店舗ID が不正です");
      return;
    }

    console.log("送信するデータ:", { user_id: userId, store_id: store.store_id });

    try {
      const response = await fetch("http://localhost:5003/favorites", {
        method: isFavorite ? "DELETE" : "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          user_id: userId,
          store_id: store.store_id,
        }),
      });

      if (!response.ok) {
        const errorMessage = await response.text();
        throw new Error(`お気に入りの更新に失敗しました: ${errorMessage}`);
      }

      setIsFavorite(!isFavorite);
    } catch (error) {
      console.error("お気に入り更新エラー:", error);
      setError("お気に入りの更新に失敗しました");
    }
  };


  useEffect(() => {
    const fetchStores = async () => {
      try {
        const response = await fetch(
          `http://localhost:5003/stores/detail/${id}`
        );
        if (!response.ok) {
          throw new Error(`サーバーエラー: ${response.status}`);
        }
        const data = await response.json();
        console.log(data);

        setStore(data);
      } catch (error) {
        console.error("店舗情報の取得に失敗しました:", error);
      }
    };
    fetchStores();
  }, [id]);

  // 店舗データとレビューを取得して設定する関数
  useEffect(() => {
    const fetchStoreWithReviews = async () => {
      try {
        const storeResponse = await fetch(`http://localhost:5003/stores/detail/${id}`);
        const reviewResponse = await fetch(`http://localhost:5003/reviews`);

        if (!storeResponse.ok || !reviewResponse.ok) {
          throw new Error("データ取得に失敗しました");
        }

        const storeData: Store = await storeResponse.json();
        const reviewData: Review[] = await reviewResponse.json();

        // 店舗に関連付けられた口コミを結び付ける
        const reviews = reviewData.filter((review) => review.store_id === storeData.store_id);
        setStore({ ...storeData, reviews });
      } catch (err: any) {
        console.error("データ取得中にエラーが発生しました:", err);
        setError("店舗情報の取得に失敗しました");
      }
    };

    fetchStoreWithReviews();
  }, [id]);



  if (error) return <div className="container">{error}</div>;
  if (!store) return <div className="container">データを読み込んでいます..</div>;


  // 店舗詳細情報の表示
  return (
    <>
    <Header/>
    <div className="container">
      <h1 className="detail-title">{store.store_name}</h1>
      {store?.store_img && store.store_img.length > 0 ? (
        <ImageSlider images={store.store_img} />
      ) : (
        <p>画像がありません</p>
      )}
      <p>
        <strong>住所: </strong>
        {store.store_address}
      </p>
      {/* Google Map 埋め込み */}
      <div style={{ margin: "20px 0" }}>
        <iframe
          title="Google Map"
          width="100%"
          height="300"
          style={{ border: "0", borderRadius: "8px" }}
          src={`https://www.google.com/maps/embed/v1/place?key=${MAP_API_KEY}&q=${encodeURIComponent(
            store.store_address
          )}`}
          allowFullScreen
        ></iframe>
      </div>
      <p>電話番号: {store.store_phone_number}</p>
      <p>営業時間: {store.store_opening_hours}</p>
      <a
        href={store.store_url}
        target="_blank"
        rel="noopener noreferrer"
        style={{
          display: "inline-block",
          marginTop: "10px",
          padding: "10px 20px",
          fontSize: "13px",
          color: "#00000",
          textDecoration: "none",
          borderRadius: "5px",
        }}
      >
        店舗の公式サイト
      </a>
    </div>
    </>
  );
};

export default PetShopDetail;
