import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

interface Store {
  store_id: number;
  store_name: string;
  store_description: string;
  store_address: string;
  store_opening_hours: number;
  store_phone_number: string;
  store_dogrun_detail: string;
  store_url: string;
  store_img: string;
  tags: string[];
  } 


  const DogRunDetail: React.FC = () => {
  const { storeId } = useParams<{ storeId: string }>();
  const [store, setStore] = useState<Store>();


   //店舗データの取得
    useEffect(() => {
      const fetchStores= async () => {
        try {
          const response = await fetch (`http://localhost:5003/stores/detail/${storeId}`);
          const data : Store = await response.json();
          setStore(data);
        } catch (error) {
          console.error("店舗情報の取得に失敗しました:", error); 
        }
      };
      fetchStores();
      }, [storeId]);


      return (
        <div
          style={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#FAF3E0",
        }}
       >
        {store.map((storeItem) => (
         <div key= {storeItem.store_id}
           style= {{ 
           padding: "20px",
           marginBottom: "30px",
           backgroundColor: "#fff",
           borderRadius: "8px",
           }}
           >
          <h1>{storeItem.store_name}</h1>
          <img src= {storeItem.store_img} alt= {storeItem.store_name} style={{ width: "100%"}} 
          />
          <p>
            {storeItem.store_description}</p>
          <p><strong>住所: </strong>{storeItem.store_address}</p>
          <p>電話番号: {storeItem.store_phone_number}</p>
          <a href={storeItem.store_url} target="_blank" rel="noopener noreferrer">
            店舗の公式サイト
          </a>
          <ul style={{ listStyleType: "none", padding: 0 }}>
            {storeItem.tags.map((tag, index) => (
              <li
                key={index}
                style={{
                  display: "inline-block",
                  margin: "5px",
                  padding: "5px 10px",
                  backgroundColor: "#ddd",
                  borderRadius: "10px",
                }}
              >
                {tag}
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};


  export default DogRunDetail;
