import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./DogRunPage.css";
import DogrunImage from "../assets/images/Dogrun/dogrun.png";
import ImageSlider from "../../ImageSlider";
import Header from "../Header";
import Footer from "../Footer";

interface Store {
  id: number;
  store_name: string;
  description: string;
  store_img: string;
  address: string;
  phone_number: string;
  opening_hours: string;
  store_url: string;
}

const DogRunPage: React.FC = () => {
  const navigate = useNavigate();
  const [stores, setStores] = useState<Store[]>([]);
  const [storeData, setStoreData] = useState<{ store_name: string; store_img: string[] } | null>(null);

  const handleClick = () => {
    navigate('/DogrunRegionsList');
  };


  useEffect(() => {
    // 特定の店舗のデータを取得
    const fetchStoreData = async () => {
      try {
        const response = await fetch("http://localhost:5003/stores/type/1");
        const data = await response.json();
        setStoreData(data);
      } catch (error) {
        console.error("データ取得エラー:", error);
      }
    };

    fetchStoreData();
  }, []);

  useEffect(() => {
    stores.forEach((storeItem) => {
      console.log("取得した店舗情報:", storeItem);
    });
  }, [stores]);

  return (
    <>
      <Header />
      <div className="dogrun-page-container">
        <p onClick={handleClick} className="search-dogrun">
          全国のドッグランを探す
        </p>
        <div>
          <img src={DogrunImage} alt="DogrunImage" className="dogrun-image" />
        </div>
        <div className="store-list">
          {stores.map((store) => (
            <div key={store.id} className="store-card">
              <img src={store.store_img} alt={store.store_name} className="store-image" />
              <h3>{store.store_name}</h3>
            </div>
          ))}
        </div>
        {storeData && storeData.store_img && storeData.store_img.length > 0 ? (
  <div>
    <ImageSlider images={storeData.store_img} />
    <h1>{storeData.store_name}</h1>
  </div>
) : (
  <p>画像データがありません。</p>
)}

      </div>
      <Footer />
    </>
  );
};

export default DogRunPage;
