import React, { useState, useEffect } from "react";
import "./AutoImageSlider.css";

interface AutoImageSliderProps {
  images: string[];
  autoPlay?: boolean; // 自動再生を有効/無効にする
  autoPlayInterval?: number; // 自動再生間隔 (ミリ秒)
}

const AutoImageSlider: React.FC<AutoImageSliderProps> = ({
  images,
  autoPlay = true,
  autoPlayInterval = 3000, // デフォルトでは3秒
}) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // 前の画像に移動
  const handlePrev = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  // 次の画像に移動
  const handleNext = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  // 自動再生
  useEffect(() => {
    if (!autoPlay) return;

    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) =>
        prevIndex === images.length - 1 ? 0 : prevIndex + 1
      );
    }, autoPlayInterval);

    return () => {
      clearInterval(interval); // クリーンアップ処理
    };
  }, [autoPlay, autoPlayInterval, images.length]);

  return (
    <div className="auto-image-slider-container">
      {/* 左矢印 */}
      <button onClick={handlePrev} className="arrow-button left-arrow">
        &lt;
      </button>

      {/* 現在の画像 */}
      <div className="image-container">
        <img
          src={images[currentImageIndex]}
          alt={`Slide ${currentImageIndex + 1}`}
          className="slider-image"
        />
      </div>

      {/* 右矢印 */}
      <button onClick={handleNext} className="arrow-button right-arrow">
        &gt;
      </button>
    </div>
  );
};

export default AutoImageSlider;
