import { link } from "fs";
import React, { useEffect, useState } from "react";


interface Favorite {
  id: number;
  user_id: number;
  store_id: number;
  store_name: string;
  store_address: string;
}

const FavoritePage: React.FC = () => {
  const [favorites, setFavorites] = useState<Favorite[]>([]);

  useEffect(() => {
    const fetchFavorites = async () => {
      try {
       const response = await fetch("http://localhost:5000/favorites/1");

        if (!response.ok ) {
         throw new Error("エラーが返されました");
        }

        const data = await response.json();
        setFavorites(data);
      }catch (error) {
        console.error(error);
      }
    };

    fetchFavorites();
  }, []);

  return (
    <div className="favorite-container">
      <header className="app-header">
        <h1>お気に入りリスト</h1>
      </header>
      <ul className="favorite-list">
        {favorites.length > 0 ? (
          favorites.map((favorite) => (
            <li key={favorite.id} className="favorite-item">
              <h2 className="favorite-title">{favorite.store_name}</h2>
              <p className="favorite-address">
                <strong>住所:</strong> {favorite.store_address}
              </p>
              <p className="favorite-id">
                <strong>ストアID:</strong> {favorite.store_id}
              </p>
            </li>
          ))
        ) : (
          <p className="no-favorites">お気に入りがまだ登録されていません。</p>
        )}
      </ul>
    </div>
  );
};


export default FavoritePage;
