import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./TopPage.css";
import HamburgerMenu from "../HamburgerMenu";
import DogrunImage from "../pages/assets/images/Dogrun/dogrun.png";
import dogcafeImage from "../pages/assets/images/Dogcafe/dogcafe.png";
import petshopImage from "../pages/assets/images/Petshop/petshop.png";
import hospitalImage from "../pages/assets/images/Hospital/hospital.png";
import dogrunNameTag from "../pages/assets/images/Dogrun/dogrun-nametag.png";
import dogCafeNameTag from "../pages/assets/images/Dogcafe/dogcafe-nametag.png";
import petshopNameTag from "../pages/assets/images/Petshop/petshop-nametag.png";
import hospitalNameTag from "../pages/assets/images/Hospital/hospital-nametag.png";
import Header from "./Header";
import Footer from "./Footer";

const TopPage: React.FC = () => {
  const [showText, setShowText] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 480);
    };

    window.addEventListener("resize", handleResize);
    handleResize(); // 初回実行

    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    setTimeout(() => {
      setShowText(true);
    }, 800);
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      const fadeElements = document.querySelectorAll<HTMLElement>(".fade-in");
      fadeElements.forEach((el) => {
        const rect = el.getBoundingClientRect();
        if (rect.top < window.innerHeight * 0.8) {
          el.classList.add("show");
        }
      });
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll();

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="TopPage-container">
      {/* ハンバーガーメニュー */}
      <HamburgerMenu />
      <Header />

      {/* メインタイトル */}
      <p className={`main-title scale-up ${showText ? "show" : ""}`}>
        ドッグラン、ドッグカフェ、ペットショップや動物病院など
        <br />
        様々なわんこの情報をご紹介しております!
      </p>
      <button
  className="main__scroll"
  onClick={() => {
    document.getElementById("target-section")?.scrollIntoView({ behavior: "smooth" });
  }}
>
  <div className="main__scroll-box">
    <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg" fill="none">
      <path d="M0 0h24v24H0z" fill="none" />
      <path d="M11.9997 13.1716L7.04996 8.22186L5.63574 9.63607L11.9997 16L18.3637 9.63607L16.9495 8.22186L11.9997 13.1716Z" />
    </svg>
  </div>
  <span className="main__scroll-text">Scroll</span>
</button>


      {/* 各セクション */}
      <div
        className={
          isMobile ? "scroll-container mobile" : "scroll-container desktop"
        }
      >
        {isMobile ? (
          // スマホ用レイアウト
          <div className="scroll-content">
            <Link to="/dogrun" className="fade-in">
              <img src={dogrunNameTag} alt="ドッグランネームタグ" />
              <img
                src={DogrunImage}
                alt="ドッグランのイラスト"
                className="item"
              />
            </Link>
            <Link to="/dogcafe" className="fade-in">
              <img src={dogCafeNameTag} alt="ドッグカフェネームタグ" />
              <img
                src={dogcafeImage}
                alt="ドッグカフェのイラスト"
                className="item"
              />
            </Link>
            <Link to="/petshop" className="fade-in">
              <img src={petshopNameTag} alt="ペットショップネームタグ" />
              <img
                src={petshopImage}
                alt="ペットショップのイラスト"
                className="item"
              />
            </Link>
            <Link to="/hospital" className="fade-in">
              <img src={hospitalNameTag} alt="病院ネームタグ" />
              <img src={hospitalImage} alt="病院のイラスト" className="item" />
            </Link>
          </div>
        ) : (
          // PC用レイアウト
          <div className="scroll-container">
            <div className="scroll-content">
              <Link to="/dogrun">
                <img
                  src={dogrunNameTag}
                  alt="ドッグランネームタグ"
                  className="item-nametag"
                />
                <img
                  src={DogrunImage}
                  alt="ドッグランのイラスト"
                  className="item"
                />
              </Link>
              <Link to="/dogcafe">
                <img
                  src={dogCafeNameTag}
                  alt="ドッグカフェネームタグ"
                  className="item-nametag"
                />
                <img
                  src={dogcafeImage}
                  alt="ドッグカフェのイラスト"
                  className="item"
                />
              </Link>
              <Link to="/petshop">
                <img
                  src={petshopNameTag}
                  alt="ペットショップネームタグ"
                  className="item-nametag"
                />
                <img
                  src={petshopImage}
                  alt="ペットショップのイラスト"
                  className="item"
                />
              </Link>
              <Link to="/hospital">
                <img
                  src={hospitalNameTag}
                  alt="病院ネームタグ"
                  className="item-nametag"
                />
                <img
                  src={hospitalImage}
                  alt="病院のイラスト"
                  className="item"
                />
              </Link>
            </div>
            <div className="scroll-content">
              <Link to="/dogrun">
