import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./DogRunPage.css";
import DogrunImage from "../assets/images/Dogrun/dogrun.png";
import Header from "../Header";
import Footer from "../Footer";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/swiper-bundle.min.css";


interface Store {
  id: number;
  name: string;
  img: string;
  description: string;
}

const DogRunPage: React.FC = () => {
  const navigate = useNavigate();
  const [stores, setStores] = useState<Store[]>([]);

  const handleClick = () => {
    navigate('/DogrunRegionsList');
  };

useEffect(()=> {
  //店舗データを取得
  const fetchStores = async () => {
    try {
      const response = await fetch("http://localhost:5003/stores");
      if(!response.ok) {
        throw new Error("店舗データの取得に失敗しました");
      }
      const data: Store[] = await response.json();
      setStores(data);
    } catch (error) {
      console.error("データ取得エラー:", error);
    }
  };

  fetchStores();
}, []);

  return (
    <>
      <Header />
      <div className="dogrun-page-container">
        <p onClick={handleClick} className="search-dogrun">
          全国のドッグランを探す
        </p>
        <div>
          <img src={DogrunImage} alt="DogrunImage" className="dogrun-image" />
        </div>
      </div>

      {/* スライドショーの追加 */}
      <div className="store-slider-container">
        <h3 className="store-slider-title">ドッグラン一覧</h3>
        <Swiper spaceBetween={20} slidesPerView={3} loop={true}>
          {stores.map((store)=> (
            <SwiperSlide key={store.id}>
              <div className="store-card">
              <img
                  src={store.img || "http://via.placeholder.com/150"}
                  alt={store.name}
                  className="store-card-image"
                  />

                  <h4 className="store-card-title">{store.name}</h4>
              </div>
            </SwiperSlide>
          ))}
        </Swiper>
        </div>
      <Footer/>
    </>

  );
};


export default DogRunPage;
