import React, { useState } from "react";
import { useModal } from "../context/ModalContext";
import "../styles/Modal.css";

const Modal: React.FC = () => {
  const { isModalOpen, closeModal, storeName } = useModal();
  const [selectedRating, setSelectedRating] = useState<number>(0);
  const [comment, setComment] = useState<string>("");
  const [error, setError] = useState<string | null>(null);

  if (!isModalOpen) return null; // モーダルが閉じている場合は何も表示しない

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (selectedRating <= 0) {
      setError("評価を入力してください。");
      return;
    }
    if (comment.trim() === "") {
      setError("口コミを入力してください。");
      return;
    }

    console.log(`投稿 - 店舗: ${storeName}, 評価: ${selectedRating}, コメント: ${comment}`);
    setSelectedRating(0);
    setComment("");
    setError(null);
    closeModal(); // モーダルを閉じる
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={closeModal}>
          &times;
        </span>
        <h1>{storeName}</h1>
        <h2>口コミを投稿</h2>
        <form onSubmit={handleSubmit}>
          <div className="rating-container">
            <label>評価:</label>
            <div className="star-rating">
              {[1, 2, 3, 4, 5].map((value) => (
                <span
                  key={value}
                  className={`star ${value <= selectedRating ? "selected" : ""}`}
                  onClick={() => setSelectedRating(value)}
                >
                  ★
                </span>
              ))}
            </div>
          </div>
          <div className="comment-container">
            <label>口コミ:</label>
            <textarea
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              placeholder="口コミを入力してください"
            />
          </div>
          {error && <p className="error-message">{error}</p>}
          <button type="submit" className="submit-button">投稿</button>
        </form>
      </div>
    </div>
  );
};

export default Modal;
