import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

interface Tag {
  id: number;
  name: string;
}

interface Store {
  store_id: number;
  store_name: string;
  store_description: string;
  store_address: string;
  store_phone_number: string;
  store_url: string;
  store_img: string;
  tags: Tag[]; // タグの配列として定義
}

const DogRunDetail: React.FC = () => {
  const { id } = useParams<{ id: string }> ();
  console.log("取得したID", id);

  const [store, setStore] = useState<Store | null>(null)
  const numericId = id? parseInt(id,10) : NaN;

  // 無効なIDの場合の処理
  if (isNaN(numericId)) {
    console.error("無効なIDが指定されました:", id);
    return <div>無効な店舗IDが指定されました。</div>;
  }


  useEffect(() => {
    const fetchStores = async () => {
      try {
        const response = await fetch (
          `http://localhost:5003/stores/detail/${numericId}`
        );
        const data = await response.json();
        setStore(data);
      } catch (error) {
        console.error("店舗情報の取得に失敗しました:", error);
        console.log ( `http://localhost:5003/stores/detail/${numericId}`)
      }
    };
    fetchStores();
  }, [numericId]);






  // storeがnullの場合の処理
  if (!store) {
    return (
      <div style={{ padding: "20px", backgroundColor: "#FAF3E0", textAlign: "center" }}>
        <p>データを読み込んでいます...</p>
      </div>
    );
  } 
  return (
    <div style={{ padding: "20px", backgroundColor: "#FAF3E0", textAlign: "center" }}>
      <h1>{store.store_name}</h1>
      <img
        src={store.store_img}
        alt={store.store_name}
        style={{ width: "100%", borderRadius: "8px" }}
      />
      <p>{store.store_description}</p>
      <p>
        <strong>住所: </strong>
        {store.store_address}
      </p>
      <p>電話番号: {store.store_phone_number}</p>
      <a
        href={store.store_url}
        target="_blank"
        rel="noopener noreferrer"
        style={{
          display: "inline-block",
          marginTop: "10px",
          padding: "10px 20px",
          backgroundColor: "#007BFF",
          color: "#fff",
          textDecoration: "none",
          borderRadius: "5px",
        }}
      >
        店舗の公式サイト
      </a>
      <h2>関連タグ</h2>
      <ul style={{ listStyleType: "none", padding: 0 }}>
        {store.tags.map((tag) => (
          <li
            key={tag.id}
            style={{
              display: "inline-block",
              margin: "5px",
              padding: "5px 10px",
              backgroundColor: "#ddd",
              borderRadius: "10px",
            }}
          >
            {tag.name}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DogRunDetail;
