import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./DogRunPage.css";
import DogrunImage from "../assets/images/Dogrun/dogrun.top.png";
import Header from "../Header";
import Footer from "../Footer";

interface Store {
  id: number;
  store_name: string;
  description: string;
  store_img: string[];
  address: string;
  phone_number: string;
  opening_hours: string;
  store_url: string;
  prefecture_name: string;
}

const DogRunPage: React.FC = () => {
  const navigate = useNavigate();
  const [stores, setStores] = useState<Store[]>([]);
  const sliderRef = useRef<HTMLDivElement>(null);
  const [isTransitioning, setIsTransitioning] = useState(true);

  const handleClick = () => {
    navigate("/DogrunRegionsList");
  };

  useEffect(() => {
    const fetchStoreData = async () => {
      try {
        const response = await fetch("http://localhost:5003/stores/type/random/1");
        if (!response.ok) {
          throw new Error(`HTTPエラー: ${response.status}`);
        }
        const data = await response.json();
        console.log("取得したデータ:", data);

        // データをそのまま設定
        setStores(data);
      } catch (error) {
        console.error("データ取得中にエラーが発生しました:", error);
      }
    };

    fetchStoreData();
  }, []);

  useEffect(() => {
    if (!sliderRef.current || stores.length === 0) return;

    const slider = sliderRef.current;

    // クローン要素を追加
    const firstClone = slider.firstElementChild!.cloneNode(true);
    const lastClone = slider.lastElementChild!.cloneNode(true);
    slider.appendChild(firstClone); // 末尾にクローン追加
    slider.insertBefore(lastClone, slider.firstElementChild); // 先頭にクローン追加

    // 初期位置をクローン要素を考慮してセット
    slider.style.transition = "none";
    slider.style.transform = `translateX(-${300}px)`;

    let startPosition = -300; // 初期位置（クローン要素分オフセット）

    const slide = () => {
      setIsTransitioning(true); // トランジション中フラグ
      startPosition -= 300; // 左に1スライド分進む

      slider.style.transition = "transform 0.5s ease-in-out";
      slider.style.transform = `translateX(${startPosition}px)`;

      slider.addEventListener("transitionend", () => {
        setIsTransitioning(false); // トランジション終了フラグ

        if (startPosition === -300 * (stores.length + 1)) {
          // 仮の最後に達したら、1番目に戻る
          slider.style.transition = "none";
          startPosition = -300;
          slider.style.transform = `translateX(${startPosition}px)`;
        }

        if (startPosition === 0) {
          // 仮の最初に達したら、最後に戻る
          slider.style.transition = "none";
          startPosition = -300 * stores.length;
          slider.style.transform = `translateX(${startPosition}px)`;
        }
      });
    };

    const intervalId = setInterval(slide, 3000); // 3秒ごとにスライド

    return () => clearInterval(intervalId); // クリーンアップ
  }, [stores]);

  return (
    <>
      <Header />
      <div className="dogrun-page-container">
        <p onClick={handleClick} className="search-dogrun">
          全国のドッグランを探す
        </p>
        <div>
          <img src={DogrunImage} alt="ドッグランのイラスト" className="dogrun-image" />
        </div>
        <div className="store-slider-container">
          <div className="dogrun-slider" ref={sliderRef}>
            {stores.map((store) => (
              <div
                key={store.id}
                className="store-card"
                onClick={() => navigate(`/store/detail/${store.id}`)}
              >
                {store.store_img.length > 0 && (
                  <img
                    src={store.store_img[0]}
                    alt={store.store_name}
                    className="store-image"
                  />
                )}
                <h3 className="slider-store-name">{store.store_name}</h3>
                <h3 className="slider-prefecture-name">{store.prefecture_name}</h3>
              </div>
            ))}
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default DogRunPage;
