import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./DogRunPage.css";
import DogrunImage from "../assets/images/Dogrun/dogrun.top.png";
import Header from "../Header";
import Footer from "../Footer";


interface Store {
  id: number;
  store_name: string;
  description: string;
  store_img: string[];
  address: string;
  phone_number: string;
  opening_hours: string;
  store_url: string;
  prefecture_name: string;
}

const DogRunPage: React.FC = () => {
  const navigate = useNavigate();
  const [loopedStores, setLoopedStores] = useState<Store[]>([]);

  const handleClick = () => {
    navigate("/DogrunRegionsList");
  };

  useEffect(() => {
    const fetchStoreData = async () => {
      try {
        const response = await fetch("http://localhost:5003/stores/type/random/1");
        const data = await response.json();

        console.log("取得したデータ:", data);

        const parsedData = data.map((store: any) => ({
          ...store,
          id: store.id || store.store_id,
          store_img: Array.isArray(store.store_img)
            ? store.store_img
            : store.store_img ? JSON.parse(store.store_img) : [],
        }));

        // ランダムに並び替え、10店舗だけ取得
        const shuffledData = [...parsedData].sort(() => 0.5 - Math.random());
        const top10Stores = shuffledData.slice(0, 10);

        console.log("選ばれた10店舗:", top10Stores);

        setLoopedStores(top10Stores); // 10店舗だけ設定
      } catch (error) {
        console.error("店舗データの取得中にエラーが発生しました:", error);
      }
    };

    fetchStoreData();
  }, []); // 依存配列は空に設定


  return (
    <>
      <Header />
      <div className="dogrun-page-container">
        <p onClick={handleClick} className="search-dogrun">
          全国のドッグランを探す
        </p>
        <div>
          <img src={DogrunImage} alt="ドッグランのイラスト" className="dogrun-image" />
        </div>
        <div className="dogrun-top-store-list">
  {loopedStores.length > 0 ? (
    loopedStores.map((store, index) => (
      <div
        key={store.id}
        className="store-card"
        onClick={() => {
          navigate(`/store/detail/${store.id}`);
        }}
      >
        {store.store_img.length > 0 && (
          <img
            src={store.store_img[0]}
            alt={store.store_name}
            className="store-image"
          />
        )}
        <h3 className="slider-store-name">{store.store_name}</h3>
        <h3 className="slider-prefecture-name">{store.prefecture_name}</h3>
      </div>
    ))
  ) : (
    <p>表示する店舗がありません。</p>
  )}

  {loopedStores.length > 0 ? (
    loopedStores.map((store, index) => (
      <div
        key={store.id}
        className="store-card"
        onClick={() => {
          navigate(`/store/detail/${store.id}`);
        }}
      >
        {store.store_img.length > 0 && (
          <img
            src={store.store_img[0]}
            alt={store.store_name}
            className="store-image"
          />
        )}
        <h3 className="slider-store-name">{store.store_name}</h3>
        <h3 className="slider-prefecture-name">{store.prefecture_name}</h3>
      </div>
    ))
  ) : (
    <p>表示する店舗がありません。</p>
  )}
</div>

      </div>
      <Footer />
    </>
  );
};

export default DogRunPage;
