import React, { useEffect, useState } from "react";


interface Favorite {
  id: number;
  user_id: number;
  store_id: number;
}

const FavoritePage: React.FC = () => {
  const [favorites, setFavorites] = useState<Favorite[]>([]);

  useEffect(() => {
    const fetchFavorites = async () => {
      try {
       const response = await fetch("http://localhost:5000/favorites/1"); // ユーザーID: 1
        if (!response.ok ) {
         throw new Error("エラーが返されました");
        }

        const data = await response.json();
        setFavorites(data);
      }catch (error) {
        console.error(error);
        alert("お気に入りリストの取得に失敗しました");
      }
    };

    fetchFavorites();
  }, []);

  return (
    <div className="favorite-container">
      <header className="App-header">
        <h1>お気に入りリスト</h1>
      </header>
      <ul>
        {favorites.length > 0 ? (
          favorites.map((favorite) => (
            <li key={favorite.id}>ストアID: {favorite.store_id}</li>
          ))
        ) : (
          <p>お気に入りがまだ登録されていません。</p>
        )}
      </ul>
    </div>
  );
};

export default FavoritePage;
