import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./FavoritePage.css";

interface Favorite {
  id: number;
  user_id: number;
  store_id: number;
  store_name: string;
  store_address: string;
  store_URL: string;
  store_img: string;
}

interface Review {
  id: number;
  store_id: number;
  rating: number;
  comment: string;
}

const FavoritePage: React.FC = () => {
  const [favorites, setFavorites] = useState<Favorite[]>([]);
  const [reviews, setReviews] = useState<Review[]>([]);
  const [averageRating, setAverageRating] = useState<number>(0);
    const [showModal, setShowModal] = useState(false);
      const [storeName, setStoreName] = useState<string>("");
        const [error, setError] = useState<string | null>(null);
         const { storeId } = useParams<{ storeId: string }>();


  useEffect(() => {
    const fetchFavorites = async () => {
      try {
        const response = await fetch("http://localhost:5003/favorites/1", {
          method: "GET",
          credentials: "include",
          headers: { "Content-Type": "application/json" },
        });

        if (!response.ok) {
          throw new Error("エラーが返されました");
        }

        const data: Favorite[] = await response.json();
        setFavorites(data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchFavorites();
  }, []);

   //口コミデータの取得

   useEffect(() => {
    const fetchReviews = async () => {
      try {
        const response = await fetch("http://localhost:5003/reviews", {
          method: "GET",
          headers: { "Content-Type" : "application/json"},
        });

        if (!response.ok) {
          throw new Error("口コミデータの取得に失敗しました");
        }

      const data: Review[] = await response.json();
      setReviews(data);
    } catch (error) {
      console.error(error);
    }
   };

   fetchReviews();
  }, []);

  // 平均評価を計算する関数
  const calculateAverageRating = (storeId: number): number => {
    const storeReviews = reviews.filter((review) => review.store_id === storeId);
    if (storeReviews.length === 0) return 0;

    const totalRating = storeReviews.reduce((sum, review) => sum + review.rating, 0);
    return totalRating / storeReviews.length;
  };
  const handleReviewSubmit = async (rating: number, comment: string) => {
    try {
      console.log("storeId:", storeId);

      const response = await fetch("http://localhost:5003/reviews", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          store_id: storeId,
          rating,
          comment,
        }),
      });

      if (!response.ok) {
        throw new Error("コメント投稿に失敗しました");
      }
      const createdReview = (await response.json()) as Review;

      setReviews((prev) => [createdReview, ...prev]);
      setError(null);
      setShowModal(false);
    } catch (error) {
      console.error(error);
      setError("コメント投稿に失敗しました");
    }
  };

  return (
    <div className="favorite-container">
      <header className="app-header">
        <h1 className="title">お気に入りリスト</h1>
      </header>

      <ul className="favorite-list">
        {favorites.length > 0 ? (
          favorites.map((favorite) => {
            const averageRating = calculateAverageRating(favorite.store_id);

            return (
              <li key={favorite.id} className="favorite-item">
                <Link to={`/dogrun/detail/${favorite.store_id}`} className="favorite-link">
                  <img
                    src={
                      favorite.store_img
                        ? favorite.store_img
                        : "http://via.placeholder.com/150"
                    }
                    alt={favorite.store_name}
                    className="favorite-image"
                  />
                  <h2 className="favorite-title">{favorite.store_name}</h2>
                </Link>

                {/* 平均評価の表示 */}
                <div className="average-rating">
                  {[1, 2, 3, 4, 5].map((value) => (
                    <span
                      key={`star-${favorite.store_id}-${value}`}
                      className={`star ${value <= Math.round(averageRating) ? "selected" : ""}`}
                    >
                      ★
                    </span>
                  ))}
                        {showModal && (
        <Modal
          show={showModal}
          onClose={() => setShowModal(false)}
          onSubmit={(rating: number, comment: string) => handleReviewSubmit(rating, comment)}
          storeName={storeName} 
        />
      )}
                </div>
              </li>
            );
          })
        ) : (
          <p className="no-favorites">お気に入りがまだ登録されていません。</p>
        )}
      </ul>
    </div>
  );
};


export default FavoritePage;
