import React, { useState } from "react";
import { useModal } from "../context/ModalContext";
import "../components/Modal.css";


const Modal: React.FC = () => {
  const { isModalOpen, closeModal, storeName } = useModal();
  const [selectedRating, setSelectedRating] = useState<number>(0);
  const [comment, setComment] = useState<string>("");
  const [error, setError] = useState<string | null>(null);

  if (!isModalOpen) return null;

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (selectedRating <= 0) {
      setError("評価を入力してください。");
      return;
    }
    if (comment.trim() === "") {
      setError("口コミを入力してください。");
      return;
    }

    console.log(`投稿 - 店舗: ${storeName}, 評価: ${selectedRating}, コメント: ${comment}`);
    setSelectedRating(0);
    setComment("");
    setError(null);
    closeModal();
  };

  const handleStarClick = (value: number) => {
    setSelectedRating(value);
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={closeModal}>
          &times;
        </span>
        <h1>{storeName}</h1>
        <h2>口コミを投稿</h2>
        <form onSubmit={handleSubmit} style={{ marginTop: "20px" }}>
          <div style={{ marginBottom: "10px" }}>
            <label>
              評価:
              <div className="star-rating">
                {[1, 2, 3, 4, 5].map((value) => (
                  <span
                    key={value}
                    className={`star ${value <= selectedRating ? "selected" : ""}`}
                    onClick={() => handleStarClick(value)}
                  >
                    ★
                  </span>
                ))}
              </div>
            </label>
          </div>
          <div style={{ marginBottom: "10px" }}>
            <label>
              口コミ:
              <textarea
                value={comment}
                onChange={(e) => setComment(e.target.value)}
                style={{ width: "100%", height: "80px", marginLeft: "10px" }}
              />
            </label>
          </div>
          {error && <p style={{ color: "red", marginBottom: "10px" }}>{error}</p>}
          <button type="submit" style={{ padding: "8px 16px", borderRadius: "4px" }}>
            投稿
          </button>
        </form>
      </div>
    </div>
  );
};

export default Modal;
