import React, {useState} from "react";
import { Link, useParams, useNavigate,useLocation } from "react-router-dom";
import "./HospitalPage.css";
import hospital from "../assets/images/hospital.png";

const prefectureNames: { [key:string] : string } = {
  hokkaido: "北海道",
  tohoku: "東北地方",
  kanto: "関東地方",
  chubu: "中部地方",
  kansai: "関西地方",
  chugoku: "中国地方",
  shikoku: "四国地方",
  kyushu: "九州地方",
  okinawa: "沖縄",

};

// hospital コンポーネント（都道府県別のページ）
const HospitalPage = () => {
  const { prefecture } = useParams<{ prefecture: string }>(); 
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const [ selectedOptions,setselectedOptions ] = useState<string[]>([]);
  const [isDropdownVisible,setIsDropdownVisible] = useState(false)
  const displayPrefecture = prefectureNames[prefecture || "" ] || prefecture;
  const navigate = useNavigate ();
  const toggleDropdown = () => {
    setIsDropdownVisible(!isDropdownVisible);
  };
  const handleOptionClick = (option: string) => {
    setselectedOptions((prevSelected: string[]) =>
      prevSelected.includes(option)
        ? prevSelected.filter((item:string) => item ! == option)// 選択済みのオプションを削除
        : [...prevSelected, option] // 新しいオプションを追加
    );
  };


  return (
    <div className="hospital-page">
     <h2 className="hospital-title">全国の動物病院を探す</h2>
     <div className="hospital-img">
      <img src={hospital} 
      alt="病院のイラスト"
      className="hospital-illust" />
     </div>
    </div>
  );
};
export default HospitalPage;
