import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

interface Store {
  store_id: number;
  store_name: string;
  store_description: string;
  store_address: string;
  store_phone_number: string;
  store_url: string;
  store_img: string;
}

interface Tag {
  id: number;
  name: string;
  tag_type: number;
}

const DogRunStoreList: React.FC = () => {
  const { prefectureId } = useParams<{ prefectureId: string }>();
  const [stores, setStores] = useState<Store[]>([]);
  const [type1Tags, setType1Tags] = useState<Tag[]>([]);
  const [type2Tags, setType2Tags] = useState<Tag[]>([]);
  const [selectedTagIds, setSelectedTagIds] = useState<number[]>([]);

  // タグを取得
  useEffect(() => {
    const fetchTags = async () => {
      try {
        const response = await fetch("http://localhost:5003/tags");
        const data: Tag[] = await response.json();
        setType1Tags(data.filter((tag) => tag.tag_type === 1));
        setType2Tags(data.filter((tag) => tag.tag_type === 2));
      } catch (error) {
        console.error("タグ情報の取得に失敗しました:", error);
      }
    };
    fetchTags();
  }, []);

  // 店舗データを取得
  useEffect(() => {
    const fetchStores = async () => {
      try {
        let url;
        if (selectedTagIds.length === 0) {
          // タグが選択されていない場合は全店舗を取得
          url = `http://localhost:5003/stores/list/${prefectureId}`;
        } else {
          // タグが選択されている場合はフィルタリング
          url = `http://localhost:5003/stores/list/tag/${prefectureId}?tagIds=${selectedTagIds.join(",")}`;
        }

        const response = await fetch(url);
        const data = await response.json();
        setStores(data);
      } catch (error) {
        console.error("店舗データの取得に失敗しました:", error);
      }
    };
    fetchStores();
  }, [prefectureId, selectedTagIds]);

  // タグ選択のハンドリング
  const handleTagClick = (tagId: number) => {
    setSelectedTagIds((prev) =>
      prev.includes(tagId) ? prev.filter((id) => id !== tagId) : [...prev, tagId]
    );
  };

  return (
    <div style={{ textAlign: "center", padding: "20px", backgroundColor: "#FAF3E0" }}>
      <h2>ドッグランの条件を絞り込む</h2>
      <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", gap: "10px" }}>
        {type1Tags.map((tag) => (
          <button
            key={tag.id}
            onClick={() => handleTagClick(tag.id)}
            style={{
              backgroundColor: selectedTagIds.includes(tag.id) ? "#D1E8E1" : "#FFF",
              color: "#333",
              padding: "10px 15px",
              border: "1px solid #333",
              borderRadius: "20px",
              cursor: "pointer",
            }}
          >
            {tag.name}
          </button>
        ))}
      </div>
      <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", gap: "10px", marginTop: "20px" }}>
        {type2Tags.map((tag) => (
          <button
            key={tag.id}
            onClick={() => handleTagClick(tag.id)}
            style={{
              backgroundColor: selectedTagIds.includes(tag.id) ? "#D1E8E1" : "#FFF",
              color: "#333",
              padding: "10px 15px",
              border: "1px solid #333",
              borderRadius: "20px",
              cursor: "pointer",
            }}
          >
            {tag.name}
          </button>
        ))}
      </div>
      <div style={{ marginTop: "30px" }}>
        {stores.map((store) => (
          <div key={store.store_id} style={{ marginBottom: "20px" }}>
            <h3>{store.store_name}</h3>
            <p>{store.store_description}</p>
            <p>住所: {store.store_address}</p>
            <p>電話: {store.store_phone_number}</p>
            <a href={store.store_url} target="_blank" rel="noopener noreferrer">
              詳細を見る
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DogRunStoreList;
