import React, { useEffect, useState } from "react";
import "./Button.css";

const Button: React.FC = () => {
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsVisible(false); // 3秒後に非表示
    }, 3000);

    return () => clearTimeout(timer); // クリーンアップタイマー
  }, []);

  if (!isVisible) return null; // 非表示時にレンダリングしない

  return (
    <div className="main__action">
      <a className="main__scroll" href="#target-section">
        <div className="main__scroll-box">
          <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path d="M0 0h24v24H0z" fill="none" />
            <path
              d="M11.9997 13.1716L7.04996 8.22186L5.63574 9.63607L11.9997 16L18.3637 9.63607L16.9495 8.22186L11.9997 13.1716Z"
              fill="rgba(28,28,30,1)"
            />
          </svg>
        </div>
        <span className="main__scroll-text">Scroll</span>
      </a>
    </div>
  );
};

export default Button;
