import React, { useEffect, useState } from "react";

interface Store {
  store_id: number;
  store_name: string;
  store_description: string;
  store_address: string;
  store_phone_number: string;
  store_url: string;
  store_img: string;
  tags: string[];
}

const DogRunDetail: React.FC = () => {
  const [store, setStore] = useState<Store[]>([]); // 配列型を使用
  const [isLoading, setIsLoading] = useState(true); // ローディング状態
  const [error, setError] = useState<string | null>(null); // エラー状態

  useEffect(() => {
    const fetchStores = async () => {
      try {
        const response = await fetch(`http://localhost:5003/stores`); // APIエンドポイント
        if (!response.ok) {
          throw new Error("データの取得に失敗しました");
        }
        const data: Store[] = await response.json();
        setStore(data);
        setError(null); // エラーをクリア
      } catch (err) {
        setError("店舗情報の取得に失敗しました。");
        console.error(err);
      } finally {
        setIsLoading(false); // ローディング終了
      }
    };

    fetchStores();
  }, []);

  // ローディング中の表示
  if (isLoading) {
    return <div>ロード中...</div>;
  }

  // エラー時の表示
  if (error) {
    return <div>{error}</div>;
  }

  // データがない場合の表示
  if (store.length === 0) {
    return <div>店舗データが見つかりません</div>;
  }

  // データがある場合の表示
  return (
    <div
      style={{
        textAlign: "center",
        padding: "20px",
        backgroundColor: "#FAF3E0",
      }}
    >
      {store.map((storeItem) => (
        <div
          key={storeItem.store_id}
          style={{
            padding: "20px",
            marginBottom: "30px",
            backgroundColor: "#fff",
            borderRadius: "8px",
          }}
        >
          <h1>{storeItem.store_name}</h1>
          <img
            src={storeItem.store_img}
            alt={storeItem.store_name}
            style={{ width: "100%", borderRadius: "8px" }}
          />
          <p>{storeItem.store_description}</p>
          <p>
            <strong>住所: </strong>
            {storeItem.store_address}
          </p>
          <p>電話番号: {storeItem.store_phone_number}</p>
          <a
            href={storeItem.store_url}
            target="_blank"
            rel="noopener noreferrer"
            style={{
              display: "inline-block",
              marginTop: "10px",
              padding: "10px 20px",
              backgroundColor: "#007BFF",
              color: "#fff",
              textDecoration: "none",
              borderRadius: "5px",
            }}
          >
            店舗の公式サイト
          </a>
          <h2>関連タグ</h2>
          <ul style={{ listStyleType: "none", padding: 0 }}>
            {storeItem.tags.map((tag, index) => (
              <li
                key={index}
                style={{
                  display: "inline-block",
                  margin: "5px",
                  padding: "5px 10px",
                  backgroundColor: "#ddd",
                  borderRadius: "10px",
                }}
              >
                {tag}
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default DogRunDetail;
