import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./DogRunPage.css";
import DogrunImage from "../assets/images/Dogrun/dogrun.png";
import Header from "../Header";
import Footer from "../Footer";
import AutoImageSlider from "../../AutoImageSlider";
import GlobalImageSlider from "../../GlobalImageSlider";
import "/GlobalImageSlider.css";

interface Store {
  id: number;
  store_name: string;
  description: string;
  store_img: string[];
  address: string;
  phone_number: string;
  opening_hours: string;
  store_url: string;
}

const DogRunPage: React.FC = () => {
  const navigate = useNavigate();
  const [stores, setStores] = useState<Store[]>([]);
  const [allImages, setAllImages] = useState<string[]>([]);

  const handleClick = () => {
    navigate("/DogrunRegionsList");
  };

  useEffect(() => {
    // 店舗データを取得
    const fetchStoreData = async () => {
      try {
        const response = await fetch("http://localhost:5003/stores/type/1");
        const data = await response.json();

        // store_img を JSON パースし、配列でない場合は配列に変換
        const parsedData = data.map((store: any) => ({
          ...store,
          store_img: Array.isArray(store.store_img)
            ? store.store_img
            : JSON.parse(store.store_img),
        }));
      //すべての画像をまとめる
        const allImagesArray = parsedData.flatMap((store: Store) => store.store_img);
        setStores(parsedData);
        setAllImages(allImagesArray);

      } catch (error) {
        console.error("データ取得エラー:", error);
      }
    };

    fetchStoreData();
  }, []);

  useEffect(() => {
    stores.forEach((storeItem) => {
      console.log("取得した店舗情報:", storeItem);
    });
  }, [stores]);

  return (
    <>
      <Header />
      <div className="dogrun-page-container">
        <p onClick={handleClick} className="search-dogrun">
          全国のドッグランを探す
        </p>
        <div>
          <img src={DogrunImage} alt="DogrunImage" className="dogrun-image" />
        </div>
        <div className="global-slider-container">
        <GlobalImageSlider images={allImages} autoPlayInterval={3000} />
        </div>

        <div className="store-list">
          {stores.map((store) => (
            <div key={store.id} className="store-card">
              {/* 各店舗の画像スライダー */}
              <AutoImageSlider images={store.store_img} autoPlay={true} autoPlayInterval={5000} />
              <h3>{store.store_name}</h3>
              <p>{store.description}</p>
            </div>
            ))}
            </div>
          </div>
      <Footer />
    </>
  );
};

export default DogRunPage;
