import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

interface Store {
  id: number;
  name: string;
  description: string;
  address: string;
  phone_number: string;
  store_url: string;
  store_img: string;
  tags: Tag[];
}

interface Tag {
  id:number;
  name: string
}


const DogRunStoreList: React.FC = () => {
  const { prefectureId } = useParams<{ prefectureId: string }>(); // URLから都道府県IDを取得
  const [shops, setShops] = useState<Store[]>([]);
  const [tags, setTags] = useState<Tag[]>([]);
  const [selectedTag, setSelectedTag] = useState<Tag | null>(null); 

  useEffect(() => {
    const fetchShops = async () => {
      try {
        const response = await fetch(`http://localhost:5003/stores/list/${prefectureId}`);
        if (!response.ok) {
          throw new Error("Failed to fetch shops");
        }
        const data: Store[] = await response.json();
        setShops(data);
      } catch (error) {
        console.error("ショップ情報の取得に失敗しました:", error);
      }
    };
    fetchShops();
  }, [prefectureId]);

  useEffect(() => {
    // タグ情報を取得
    const fetchTags = async () => {
      try {
        const response = await fetch(`http://localhost:5003/tags`);
        if (!response.ok) {
          throw new Error("Failed to fetch tags");
        }
        const data: Tag[] = await response.json();
        setTags(data);
      } catch (error) {
        console.error("タグ情報の取得に失敗しました:", error);
      }
    };
    fetchTags();
  }, []);
  const handleTagClick = (tag: Tag) => {
    setSelectedTag(tag.id === selectedTag?.id ? null : tag); // 同じタグを選択するとフィルタ解除
  };

    // 選択されたタグでショップリストをフィルタリング
    const filteredStores = selectedTag
    ? shops.filter((shop) => shop.tags.includes(selectedTag.name))
    : shops;

  return (
    <div>
      <h2>ドッグラン一覧</h2>

      {/* タグボタンのリスト */}
      <div>
        {tags.map((tag) => (
          <button
            key={tag.id}
            onClick={() => handleTagClick(tag.name)}
            style={{
              backgroundColor: tag.name === selectedTag ? "lightblue" : "white",
              margin: "5px",
              padding: "5px 10px",
              borderRadius: "5px",
              border: "1px solid #ccc",
            }}
          >
            {tag.name}
          </button>
        ))}
      </div>
       {/* フィルタリングされた店舗の表示 */}
      {filteredStores.length > 0 ? (
       filteredStores.map((shop) => (
          <div key={shop.id} style={{ marginBottom: "20px" }}>
            <h3>{shop.name}</h3>
            <p>{shop.description}</p>
            <p>住所: {shop.address}</p>
            <p>電話番号: {shop.phone_number}</p>
            <a href={shop.store_url} target="_blank" rel="noopener noreferrer">
              ウェブサイト
            </a>
            <br />
            <img src={shop.store_img} alt={shop.name} style={{ width: "200px", height: "auto"}} />
          </div>
        ))
      ) : (
        <p>この都道府県にはドッグラン情報がありません。</p>
      )}
    </div>
  );
};

export default DogRunStoreList;
