import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

interface Shop {
  id: number;
  name: string;
  description: string;
  address: string;
  phone_number: string;
  store_url: string;
  store_img: string;
}

interface DogCafeTag {
  id: number;
  name: string;
}

const DogCafeStoreList: React.FC = () => {
  const { prefectureId } = useParams<{ prefectureId: string }>();
  const [shops, setShops] = useState<Shop[]>([]);
  const [selectedDogCafeTagIds, setSelectedDogCafeTagIds] = useState<number[]>(
    []
  );
  const [dogCafeTags, setDogCafeTags] = useState<DogCafeTag[]>([]); // タグ一覧のステート
  const [selectedPrefecture, setSelectedPrefecture] = useState<string>("");

  // タグ一覧の取得
  useEffect(() => {
    const fetchDogCafeTags = async () => {
      try {
        const response = await fetch(`http://localhost:5003/dogcafe-tags`);
        if (!response.ok) {
          throw new Error("Failed to fetch dog cafe tags");
        }
        const data = await response.json();
        setDogCafeTags(data); // タグ一覧をステートに保存
      } catch (error) {
        console.error("タグ情報の取得に失敗しました:", error);
      }
    };

    fetchDogCafeTags();
  }, []);

  // 店舗データの取得
  useEffect(() => {
    if (selectedDogCafeTagIds.length === 0) {
      setShops([]); // タグが選択されていない場合、店舗リストをクリア
      return;
    }

    const fetchShops = async () => {
      try {
        const response = await fetch(
          `http://localhost:5003/stores/list/${selectedDogCafeTagIds.join(".")}`
        );
        if (!response.ok) {
          throw new Error("Failed to fetch shops");
        }
        const data = await response.json();
        setShops(data); // 修正: 正しいステートにデータを設定
      } catch (error) {
        console.error("ショップ情報の取得に失敗しました:", error);
      }
    };

    fetchShops();
  }, [selectedDogCafeTagIds, prefectureId]);

  // タグ選択ハンドラー
  const handleClickDogCafeTag = (dogCafeTagId: number) => {
    setSelectedDogCafeTagIds((prevSelectedDogCafeTagIds) => {
      if (prevSelectedDogCafeTagIds.includes(dogCafeTagId)) {
        // 既に選択されている場合は削除
        return prevSelectedDogCafeTagIds.filter((id) => id !== dogCafeTagId);
      } else {
        // 新しいタグを追加
        return [...prevSelectedDogCafeTagIds, dogCafeTagId];
      }
    });
  };

  // 都道府県名の設定
  useEffect(() => {
    const prefectureNames: { [key: string]: string } = {
      "1": "北海道",
      "13": "東京",
      "14": "神奈川",
      "23": "愛知",
      "26": "京都",
      "27": "大阪",
      "28": "兵庫",
    };
    setSelectedPrefecture(
      prefectureNames[prefectureId ?? ""] || "ドッグカフェ情報がありません"
    );
  }, [prefectureId]);

  return (
    <div>
      <h2>{selectedPrefecture}のドッグカフェショップ一覧</h2>
      <div>
        <h3>タグで絞り込み</h3>
        {dogCafeTags.map((tag) => (
          <button
            key={tag.id}
            onClick={() => handleClickDogCafeTag(tag.id)}
            style={{
              marginRight: "10px",
              backgroundColor: selectedDogCafeTagIds.includes(tag.id)
                ? "lightblue"
                : "white",
            }}
          >
            {tag.name}
          </button>
        ))}
      </div>
      {shops.length > 0 ? (
        shops.map((shop) => (
          <div key={shop.id} style={{ marginBottom: "20px" }}>
            <h3>{shop.name}</h3>
            <p>{shop.description}</p>
            <p>住所: {shop.address}</p>
            <p>電話番号: {shop.phone_number}</p>
            <a href={shop.store_url} target="_blank" rel="noopener noreferrer">
              ウェブサイト
            </a>
            <br />
            <img
              src={shop.store_img}
              alt={shop.name}
              style={{ width: "200px", height: "auto" }}
            />
          </div>
        ))
      ) : (
        <p>この都道府県にはショップがありません。</p>
      )}
    </div>
  );
};

export default DogCafeStoreList;
