import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./DogRunReviewList.css";
import "./Modal.css";
import Modal from "./Modal";

type Review = {
  id: number;
  name: string;
  store_id: number;
  rating: number;
  comment: string;
  date: string;
};

const ReviewList: React.FC = () => {
  const { storeId } = useParams<{ storeId: string }>();
  const [reviews, setReviews] = useState<Review[]>([]);
  const [error, setError] = useState<string | null>(null);
  const [showModal, setShowModal] = useState(false);
  const [selectedRating, setSelectedRating] = useState<number>(0); // 星評価の状態管理

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const response = await fetch(`http://localhost:5003/reviews/${storeId}`);
        const data = await response.json();
        setReviews(data);
      } catch (error) {
        console.error(error);
        setError("口コミの取得に失敗しました");
      }
    };
    fetchReviews();
  }, [storeId]);

  const handleReviewSubmit = async (rating: number , comment: string) => {
    try {
      console.log("storeId:", storeId);

      const newReviewData = {
        store_id: Number(storeId),
        rating: selectedRating, // 選択された星評価
        comment: comment.trim(),
      };

      const response = await fetch("http://localhost:5003/reviews", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newReviewData),
      });

      if (!response.ok) {
        throw new Error("コメント投稿に失敗しました");
      }
      const createdReview = (await response.json()) as Review;

      setReviews((prev) => [...prev, createdReview]);
      setError(null);
      setShowModal(false); // モーダルを閉じる
    } catch (error) {
      console.error(error);
      setError("コメント投稿に失敗しました");
    }
  };
//星の表示
  const handleStarClick = (value: number) => {
    setSelectedRating(value); // 星評価を更新
  };

  let content;
  if (error && reviews.length === 0) {
    content = <div>{error}</div>;
  } else if (reviews.length === 0) {
    content = <div style={{ padding: "20px" }}>まだ口コミはありません</div>;
  } else {
    content = (
      <>
        {reviews.map((review) => (
          <div key={review.id} className="review-card">
            <p className="review-rating">
              <strong>評価:</strong> {review.rating}
            </p>
            <p className="review-comment">
              <strong>コメント:</strong> {review.comment}
            </p>
          </div>
        ))}
      </>
    );
  }

  return (
    <div className="review-container">
         <div className="star-rating">
             {[1, 2, 3, 4, 5].map((value) => (
            <span
              key={value} // ユニークな文字列として指定
              className={`star ${value <= selectedRating ? "selected" : ""}`}
              onClick={() => handleStarClick(value)}
            > 
             ★
            </span>

            ))}
          </div>
      <h2 className="review-title">口コミ一覧</h2>
      <button onClick={() => setShowModal(true)} style={{ marginTop: "20px" }}>
        投稿
      </button>
      {error && reviews.length > 0 && (
        <p style={{ color: "red" }}>{error}</p>
      )}
      {content}
      {showModal && (
        <div>
          <Modal
            show={showModal}
            onClose={() => setShowModal(false)}
            onSubmit={(comment) => handleReviewSubmit}
          />
        </div>
      )}
    </div>
  );
};

export default ReviewList;
