import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./DogRunPage.css";
import DogrunImage from "../assets/images/Dogrun/dogrun.top.png";
import Header from "../Header";
import Footer from "../Footer";

interface Store {
  id: number;
  store_name: string;
  description: string;
  store_img: string[];
  address: string;
  phone_number: string;
  opening_hours: string;
  store_url: string;
  prefecture_name: string;
}

const DogRunPage: React.FC = () => {
  const navigate = useNavigate();
  const [loopedStores, setLoopedStores] = useState<Store[]>([]);
  const sliderRef = useRef<HTMLDivElement>(null);

  const handleClick = () => {
    navigate("/DogrunRegionsList");
  };

  useEffect(() => {
    const fetchStoreData = async () => {
      try {
        const response = await fetch("http://localhost:5003/stores/type/random/1");
        if (!response.ok) {
          throw new Error(`HTTPエラー: ${response.status}`);
        }

        const data = await response.json();
        console.log("取得したデータ:", data);

        // ループのために配列を複製
        setLoopedStores([...data, ...data]); // データを2倍にしてループ表示
      } catch (error) {
        console.error("データ取得中にエラーが発生しました:", error);
      }
    };

    fetchStoreData();
  }, []);

  useEffect(() => {
    if (!sliderRef.current || loopedStores.length === 0) return;

    const slider = sliderRef.current;
    let startPosition = 0;

    const animate = () => {
      // 左方向に少しずつスクロール
      startPosition -= 1;
      if (Math.abs(startPosition) >= slider.scrollWidth / 2) {
        startPosition = 0; // 初期位置に戻る
      }
      slider.style.transform = `translateX(${startPosition}px)`;
      requestAnimationFrame(animate); // アニメーションを継続
    };

    // アニメーション開始
    const animationId = requestAnimationFrame(animate);

    return () => cancelAnimationFrame(animationId); // クリーンアップ
  }, [loopedStores]);

  return (
    <>
      <Header />
      <div className="dogrun-page-container">
        <p onClick={handleClick} className="search-dogrun">
          全国のドッグランを探す
        </p>
        <div>
          <img src={DogrunImage} alt="ドッグランのイラスト" className="dogrun-image" />
        </div>
        <div className="dogrun-slider-container">
          <div className="dogrun-slider" ref={sliderRef}>
            {loopedStores.map((store, index) => (
              <div
                key={`${store.id}-${index}`}
                className="store-card"
                onClick={() => {
                  navigate(`/store/detail/${store.id}`);
                }}
              >
                {store.store_img.length > 0 && (
                  <img
                    src={store.store_img[0]}
                    alt={store.store_name}
                    className="store-image"
                  />
                )}
                <h3 className="slider-store-name">{store.store_name}</h3>
                <h3 className="slider-prefecture-name">{store.prefecture_name}</h3>
              </div>
            ))}
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default DogRunPage;
